// TODO: import useFormik from formik library
import React from 'react';
import {useFormik} from 'formik'

  // TODO: add a const called formik assigned to useFormik()

  function App() {
    const formik = useFormik({
      initialValues: {
        name: '',
        email: '',
        password: ''
      },
      onSubmit: values =>{
        console.log('form: ', values);
      },
      validate: values =>{
        let errors = {};
        if(!values.name) errors.name = 'Field Required';
        if(!values.email) errors.email = 'Username should be an email';
        if(!values.password) errors.password = 'Field Required';
        return errors;
      }
    })

  return (
    <div>
      <form onSubmit={formik.handleSubmit}>
        <div>Name</div>
        <input name="name" type="text" onChange={formik.handleChange} 
        value={formik.values.name}/>
          <div>Email</div>
          <input name="email" type="text" onChange={formik.handleChange} 
        value={formik.values.email}/>
          <div>Password</div>
          <input name="password" type="text" onChange={formik.handleChange} 
        value={formik.values.password}/>
            <button type="submit">Submit</button>
            </form>
    </div>
    );
  }

  export default App;
