// TODO: import useFormik from formik library
import React from 'react';
import {useFormik} from 'formik'

  // TODO: add a const called formik assigned to useFormik()

  function App() {
    const formik = useFormik({
      initialValues: {
        name: 'Peter',
        email: '',
        password: ''
      },
      onSubmit: values =>{
        console.log('form: ', values);
        alert("Login Successful")
      },
      validate: values =>{
        let errors = {};
        if(!values.name) errors.name = 'Field Required';
        if(!values.emailField) errors.emailField = 'Username should be an email';
        if(!values.password) errors.pswField = 'Field Required';
        return errors;
      }
    });

  return (
    <div>
      <form onSubmit={formik.handleSubmit}>
        <div>Name</div>
        <input name="name" type="text" onChange={formik.handleChange} 
        value={formik.values.name}/>
        {formik.errors.name ? <div style={{color:'red'}}>{formik.errors.name}</div> : null}   
          <div>Email</div>
          <input name="emailField" type="email" onChange={formik.handleChange} 
        value={formik.values.emailField}/>
        {formik.errors.emailField ? <div style={{color:'red'}}>{formik.errors.emailField}</div> : null}   
          <div>Password</div>
          <input name="pswField" type="text" onChange={formik.handleChange} 
        value={formik.values.pswField}/>
        {formik.errors.pswField ? <div style={{color:'red'}}>{formik.errors.pswField}</div> : null}   
            <button name="submitBtn" type="submit">Submit</button>
            </form>
    </div>
    );
  }

  export default App;
